CREATE TABLE IF NOT EXISTS confirmed_ethnicity (
    date DATE NOT NULL, 
    abbreviation VARCHAR(3) NOT NULL, 
    state VARCHAR(20) NOT NULL, /*references  province_state VARCHAR( 20 ) NOT NULL*/
    Cases_Total int(8), 
    Cases_White int(7), 
    Cases_Black int(7), 
    Cases_LatinX int(7), 
    Cases_Asian int(7), 
    Cases_AIAN int(7), 
    Cases_NHPI int(7), 
    Cases_Multiracial int(7), 
    Cases_Other int(7), 
    Cases_Unknown int(7), 
    Cases_Ethnicity_Hispanic int(7), 
    Cases_Ethnicity_NonHispanic int(7), 
    Cases_Ethnicity_Unknown int(7), 
    Deaths_Total int(7), 
    Deaths_White int(7), 
    Deaths_Black int(7), 
    Deaths_LatinX int(7), 
    Deaths_Asian int(7), 
    Deaths_AIAN int(7), 
    Deaths_NHPI int(7), 
    Deaths_Multiracial int(7), 
    Deaths_Other int(7), 
    Deaths_Unknown int(7), 
    Deaths_Ethnicity_Hispanic int(7), 
    Deaths_Ethnicity_NonHispanic int(7), 
    Deaths_Ethnicity_Unknown int(7),
    PRIMARY KEY (state),
    FOREIGN KEY (state) REFERENCES region_info(province_state)
        on delete cascade
        on update cascade 
    )ENGINE = INNODB;
